//get message topic
//V1.0
var myTopic = msg.topic;
var o=msg.payload;
msg.topic = "PrintControl.Command";
var sendmsg = false; 

//return topic as payload
//store status in a variable 
var myStatus = flow.get("status");
//((msg._event == "PrinterStageChanged")&& myTopic == "octoprint/PrinterStateChanged") && 

if(myStatus == "Init"){
    node.status({fill:"green",shape:"ring",text: myStatus});
}

// on Cancelling go to idle to wait some time because printer will heat up anyway
if(
    ((o._event == "PrinterStateChanged") 
        && (o.state_id == "OPERATIONAL") 
        && ((myStatus == "Init") || myStatus == "ON")|| (myStatus == "ERROR")) ||
    (msg.payload == "test plugin startup") 
    )
{
    flow.set("status", "PrinterIdle"); //Set wait for new print job timer
    node.status({fill:"green",shape:"ring",text: "Idle"});
    sendmsg = true; 
}
//printer printing or start print, do not react on Printing message when in status cancelling
/* in some situations when cancel from octoprint and printer goes to Cooldown (printer hot) and then restart with new print it switches off
if(
    ((o._event == "PrinterStateChanged") &&  ((o.state_id == "STARTING") 
        || ((o.state_id == "PRINTING") && (myStatus != "PrinterHot")))
    && (myStatus != "CANCELLING"))
    )*/
if(
    ((o._event == "PrinterStateChanged") &&  ((o.state_id == "STARTING") 
        || (o.state_id == "PRINTING"))
    ))
{
    flow.set("status", "Printing"); //wait for print end or cancel
        node.status({fill:"green",shape:"ring",text: "Printing"});
     sendmsg = true; 
}

//printer Error assume it is hot set a longer timer and then shutdown everything
if((o._event == "PrinterStateChanged") 
    &&  (o.state_id == "ERROR"))
{
    flow.set("status", "ERROR"); //set ERROR timer, try reconnect (if possible with mqtt)
    node.status({fill:"red",shape:"ring",text: "Error"});
    sendmsg = true; 
}

//printer Cancelling assume it is hot set a longer timer and then shutdown everything
if ((o._event == "PrinterStateChanged") && (o.state_id == "CANCELLING") )
{
    flow.set("status", "CANCELLING"); //set ERROR timer, try reconnect (if possible with mqtt)
    node.status({fill:"red",shape:"ring",text: "Cancelling"});
     sendmsg = true; 
}

//print finished  or ERROR and temperatur messages received


if( 
    (   ((o._event == "PrinterStateChanged") && (o.state_id == "FINISHING"))
    ||  ((myStatus == "ERROR") && (myTopic == "octoprint/temperature/tool0")) 
    ||  ((myTopic == "PrintControl.Timer") && (msg.payload == "IdleTimeout") && ((myStatus == "PrinterIdle") || (myStatus == "ON")))
    ||  ((myTopic == "PrintControl.Timer") && (msg.payload == "CancelTimer") && ((myStatus == "CANCELLING")))
    )
    )
{
    flow.set("status", "PrinterHot"); //wait for cooldown 
     node.status({fill:"yellow",shape:"ring",text: "Wait for cooldown"});
    sendmsg = true; 
}


//printer idle and hotend over temperature or after ERROR status and reconnect receive a temp value waiting for cooldown
if (
    (((myTopic == "octoprint/temperature/tool0") && (o.actual <= "40")) && ((myStatus == "PrinterHot") || (myStatus == "ERROR")))
    ||((myTopic == "PrintControl.Timer") && (msg.payload == "PrinterHotTimeout"))
){
    flow.set("status", "PrinterCooledDown"); //Reset ERROR timer, set wait for new print job timer
    node.status({fill:"yellow",shape:"ring",text: "ShutDown Octopi"});
    sendmsg = true; 
}
//octoprint shut down finished
if (
    (msg._event == "ClientClosed") ||  (o._event == "Disconnected") ||  (o._event == "ClientClosed") || (msg.payload == "eOctoprintDown")
    )  {
    flow.set("status", "OctoprintDown");
    node.status({fill:"red",shape:"ring",text: "Disconnected"});
     sendmsg = true; 
}

if ((myTopic == "stat/SONOFFI3/POWER") && (msg.payload == "OFF"))
{
     flow.set("status", "OFF");
     node.status({fill:"red",shape:"ring",text: "OFF"});

}

if ((myTopic == "stat/SONOFFI3/POWER") && (msg.payload == "ON")
    && (myStatus != "OctoprintDown")
)
{
     flow.set("status", "ON");
     node.status({fill:"green",shape:"ring",text: "ON"});
     sendmsg = true;

}


if (sendmsg == true) 
{
        msg.payload = flow.get("status");
        sendmsg = false;
        return msg;
}

