if(topic == "Sensor6/fTempB")
{

    /*
    Temperatur über 0 ° C: k2=17.62, k3=243.12
    Temperatur 0 ° C oder darunter: k2=22.46, k3=272.62
    Temperatur t, Luftfeuchtigkeit l
    Taupunkt = k3*((k2*t)/(k3+t)+ln(l/100))/((k2*k3)/(k3+t)-ln(l/100))
    */
    var r = parseFloat(context.get("fOutHum"));
    var t=parseFloat(msg.payload);

    var k2, k3;
    var TD;
    if(t > 0)
    {
        k2=17.62;
        k3=243.12;
    }
    else
    {
        k2=22.46;
        k3=272.62;
    }

    TD = k3*((k2*t)/(k3+t)+ Math.log(r/100))/((k2*k3)/(k3+t)-Math.log(r/100));
    TD = TD.toFixed(2);
    msg.payload={
            fDewP: Number(TD),
        }


        return msg;
}

